namespace Magedelight\Vendor\Block\Adminhtml;

/**
 * Adminhtml vendor block
 */
class Vendor extends \Magento\Backend\Block\Widget\Grid\Container
{
    /**
     * Block constructor
     *
     * @return void
     */
    protected function _construct()
    {
        $this->_controller = 'adminhtml_vendor';
        $this->_blockGroup = 'Magedelight_Vendor';
        $this->_headerText = __('Manage Vendors');

        parent::_construct();

        if ($this->_isAllowedAction('Magedelight_Vendor::vendor_manage')) {
            $this->buttonList->update('add', 'label', __('Add New Vendor'));
        } else {
            $this->buttonList->remove('add');
        }
    }

    /**
     * Check permission for passed action
     *
     * @param string $resourceId
     * @return bool
     */
    protected function _isAllowedAction($resourceId)
    {
        return $this->_authorization->isAllowed($resourceId);
    }
}
